INSERT INTO `notice` VALUES (1, '烟雨朦胧中，油纸伞下的邂逅。西湖的涟漪，是爱情最初的模样。', '杭州西湖', '2024-04-05');
INSERT INTO `notice` VALUES (2, '雪花纷飞的圣诞夜，你如礼物般降临。麋鹿铃声回荡，是心动的旋律。', '哈尔滨冰雪大世界', '2023-12-25');
INSERT INTO `notice` VALUES (3, '大漠孤烟直，长河落日圆。在这片荒芜中，遇见你是最美的奇迹。', '敦煌鸣沙山', '2023-08-17');
INSERT INTO `notice` VALUES (4, '当阳光亲吻海浪，我在沙滩写下对你的思念。潮起潮落，不变的是心底的悸动。', '三亚湾', '2023-05-12');
INSERT INTO `notice` VALUES (5, '梧桐叶飘落的瞬间，我听见时光的声音。在这个转角，我们的故事悄然开始。', '南京美龄宫', '2022-10-23');
INSERT INTO `notice` VALUES (6, '人生的选择本就不正确，我不愿回头也不愿批判当时的自己。', '湖南湘潭', '2022-04-12');
INSERT INTO `notice` VALUES (7, '风很大树会因为留不住叶子而哭泣吗?', '北京天安门', '2024-07-23');
